
def bitwise_add(str1, str2):
    length = len(str1)
    result = [0] * length
#    print(result)
    inc = 0
    for i in reversed(range(length)):
        add = int(str1[i]) + int(str2[i]) + inc
        if add == 3:
            result[i] = "1"
            inc = 1
        elif add == 2:
            result[i] = "0"
            inc = 1
        elif add == 1:
            result[i] = "1"
            inc = 0
        elif add == 0:
            result[i] = "0"
            inc = 0
    return result

def bitwise_block_add(str1, blk_size):
    length = len(str1)
    str2 = "0" * (blk_size - 1) + "1"
    print(str2)
    result = [0] * length
#    print(result)
    inc = 0
    for j in reversed(range(int(length / blk_size))):
        for i in reversed(range(blk_size)):
            add = int(str1[i + j * blk_size]) + int(str2[i]) + inc
            if add == 3:
                result[i + j * blk_size] = "1"
                inc = 1
            elif add == 2:
                result[i + j * blk_size] = "0"
                inc = 1
            elif add == 1:
                result[i + j * blk_size] = "1"
                inc = 0
            elif add == 0:
                result[i + j * blk_size] = "0"
                inc = 0
    return result




